string isBalanced(string s) { stack <char> stak; string::iterator it; it = s.begin(); char popped; for(it = s.begin();it<s.end();it++) { if(s[it] = '{' || s[it] = '[' || s[it] = '(') { stak.push(s[it]); continue; } if(stak.empty()) { return 'NO'; } switch(s[it]) { case '}': popped = stak.top(); stak.pop(); if(popped = '('|| popped ='[') return 'NO'; break; case ')': popped = stak.top(); stak.pop(); if(popped = '{'|| popped ='[') return 'NO'; break; case ']': popped = stak.top(); stak.pop(); if(popped = '{'|| popped ='(') return 'NO'; break; } } if(stak.empty()) { return 'YES'; } return 'NO'; }